import java.io.*;
import java.util.Scanner;

public class floyd {

    public static void main(String[] args) throws FileNotFoundException {
        if(args.length != 1) {
            System.err.println("Usage: floyd <graph-file>");
            return;
        }

        String inputFile = args[0];
        Scanner scanner = new Scanner(new File(inputFile));
        PrintWriter writer = new PrintWriter("output.txt");

        // Process each problem in the file
        while (scanner.hasNextInt()) {
            int n = scanner.nextInt(); // Read the number of vertices/cities
            int[][] dist = new int[n][n]; // Distance matrix
            int[][] next = new int[n][n]; // Next matrix to reconstruct paths

            // Initialize matrices
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    dist[i][j] = scanner.nextInt();
                    if(dist[i][j] != 0) {
                        next[i][j] = j;
                    }
                }
            }

            // Floyd’s Algorithm
            for (int k = 0; k < n; ++k) {
                for (int i = 0; i < n; ++i) {
                    for (int j = 0; j < n; ++j) {
                        if (dist[i][j] > dist[i][k] + dist[k][j]) {
                            dist[i][j] = dist[i][k] + dist[k][j];
                            next[i][j] = next[i][k];
                        }
                    }
                }
            }

            writer.println("Problem" + ": n = " + n);
            writer.println("Pmatrix:");
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    writer.print(next[i][j] + "  ");
                }
                writer.println();
            }

            // Reconstructing paths
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    if (i != j) {
                        int u = i;
                        String path = "V" + (i+1);
                        while(u != j){
                            u = next[u][j];
                            path += " V" + (u+1);
                        }
                        writer.println("V" + (i+1) + "-V" + (j+1) + ": shortest path and length");
                        writer.println(path + ": " + dist[i][j]);
                    }
                }
            }
        }
        scanner.close();
        writer.close();
    }
}

